sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var Choices = ['Official', 'Unofficial', 'Order'];
    jslog('This is JSLOg message');
	console.log('This is console log message');
    if (newValue == '3') {
        for (var i = 0; i < Choices.length; i++) {
            g_form.addOption('u_request_type', i, Choices[i]);
        }
    } else {
        // don't use clearOptions or clearValue() methods to this as clearOptions() method removes all the options and ClearValue() clears the value but it does not remove option and user can reselect the option. 
        for (var x = 0; x < Choices.length; x++) {
            g_form.removeOption('u_request_type', x);
        }
    }
    try {
        HelloWorld();
    } catch (err) {
        jslog('a Javascript run time error occured: ' + err.message);
    }
}
